import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

// Importing from flat 'pages/' folder
import IndustriesOverview from "./pages/IndustriesOverview";
import IndustriesStats from "./pages/IndustriesStats";
import IndustriesChallenges from "./pages/IndustriesChallenges";
import Logistics from "./pages/Logistics";
import OilGas from "./pages/OilGas";

function App() {
  return (
    <Router>
      <div className="min-h-screen bg-white text-gray-800 font-sans">
        {/* Header */}
        <header className="p-4 bg-gradient-to-r from-white to-purple-100 shadow-md flex flex-col md:flex-row justify-between items-center">
          <h1 className="text-xl font-bold mb-2 md:mb-0">Industry Solutions</h1>
          <nav className="flex flex-wrap justify-center gap-4 text-sm">
            <Link to="/" className="text-gray-700 hover:text-purple-600">Overview</Link>
            <Link to="/industries/stats" className="text-gray-700 hover:text-purple-600">Stats</Link>
            <Link to="/industries/challenges" className="text-gray-700 hover:text-purple-600">Challenges</Link>
            <Link to="/logistics" className="text-gray-700 hover:text-purple-600">Logistics</Link>
            <Link to="/oil-gas" className="text-gray-700 hover:text-purple-600">Oil & Gas</Link>
          </nav>
        </header>

        {/* Routes */}
        <main>
          <Routes>
            <Route path="/" element={<IndustriesOverview />} />
            <Route path="/industries/overview" element={<IndustriesOverview />} />
            <Route path="/industries/stats" element={<IndustriesStats />} />
            <Route path="/industries/challenges" element={<IndustriesChallenges />} />
            <Route path="/logistics" element={<Logistics />} />
            <Route path="/oil-gas" element={<OilGas />} />
          </Routes>
        </main>

        {/* Footer */}
        <footer className="py-6 bg-gray-900 text-center text-white text-sm">
          Â© 2025 NeuraLogic. All rights reserved.
        </footer>
      </div>
    </Router>
  );
}

export default App;
