import React from 'react';
import {
  FaUserMd,
  FaMoneyBillWave,
  FaMicrochip,
  FaHospitalAlt,
} from 'react-icons/fa';

// Gradient Icon Wrapper Component
function GradientIcon({ children }) {
  return (
    <span className="bg-gradient-to-tr from-blue-500 via-purple-500 to-pink-500 inline-block text-transparent bg-clip-text text-3xl ml-4">
      {children}
    </span>
  );
}

// Main Healthcare Component
function HealthCare() {
  return (
    <div className="min-h-screen bg-white text-gray-800 font-sans">
      {/* Hero Section */}
      <section className="text-center py-16 px-4">
        <h2 className="text-5xl font-light">Healthcare Transformation Solutions</h2>
        <p className="mt-4 text-dark max-w-2xl mx-auto">
          Transforming healthcare with AI, XR, and Digital Learning to solve workforce gaps, control cost, & boost patient engagement through immersive, intelligent, & scalable solutions.
        </p>
        <h1 className="mt-4 max-w-2xl mx-auto text-xl font-medium">Explore the Transformation Landscape</h1>
      </section>

      {/* Stats Section */}
      <section className="max-w-4xl mx-auto px-4 py-12">
        {/* First Row */}
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center space-x-3">
            <h3 className="text-3xl font-bold">77%</h3>
            <p className="text-sm text-gray-600">Executives prioritize AI</p>
          </div>
          <div className="h-6 w-0.5 bg-gray-400"></div>
          <div className="flex items-center space-x-3">
            <h3 className="text-3xl font-bold">100K+</h3>
            <p className="text-sm text-gray-600">Worker shortage by 2027</p>
          </div>
        </div>

        {/* Second Row */}
        <div className="flex items-center justify-between mt-6">
          <div className="flex items-center space-x-3">
            <h3 className="text-3xl font-bold">46B</h3>
            <p className="text-sm text-gray-600">Market growth by 2027</p>
          </div>
          <div className="h-6 w-0.5 bg-gray-400"></div>
          <div className="flex items-center space-x-3">
            <h3 className="text-3xl font-bold">68%</h3>
            <p className="text-sm text-gray-600">Medical schools using AI</p>
          </div>
        </div>
      </section>

      {/* Industry Challenges Section */}
      <section className="bg-white py-12 px-4">
        <div className="max-w-7xl mx-auto">
          <h2 className="text-3xl font-bold text-gray-900 mb-2">Industry Challenges & Priorities</h2>
          <h3 className="text-xl text-gray-800 mb-4">Overview</h3>
          <p className="text-gray-700 mb-10 max-w-2xl">
            The healthcare industry is evolving through AI, XR, & digital learning – driving smarter care, cost efficiency,
            and better patient engagement. Amid workforce & tech challenges, digital solutions empower providers to innovate and scale resiliently.
          </p>

          <div className="flex flex-col md:flex-row justify-between items-start gap-10">
            <div className="md:w-1/2"></div>

            <div className="md:w-1/2 w-full">
              <h3 className="text-2xl font-medium text-left mb-6">Critical Challenges</h3>
              <div className="grid md:grid-cols-2 gap-6">
                <ChallengeCard
                  icon={<GradientIcon><FaUserMd /></GradientIcon>}
                  title="Workforce Crisis"
                  description="100K+ critical worker shortage by 2028, 50% burnout rate persists"
                />
                <ChallengeCard
                  icon={<GradientIcon><FaMoneyBillWave /></GradientIcon>}
                  title="Financial Pressure"
                  description="8% cost increases, <2% operating margins, $100B underpayment gap"
                />
                <ChallengeCard
                  icon={<GradientIcon><FaMicrochip /></GradientIcon>}
                  title="Technology Gaps"
                  description="180+ ransomware attacks, 137TB daily data, workflow inefficiencies"
                />
                <ChallengeCard
                  icon={<GradientIcon><FaHospitalAlt /></GradientIcon>}
                  title="Access Barriers"
                  description="26-day wait times, 4 in 10 can't afford care, cost-driven skipping"
                />
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Strategic Priorities 2025 */}
      <section className="py-12 px-4">
        <h2 className="text-3xl font-semibold text-gray-800 text-center mb-8">Strategic Priorities 2025</h2>
        <div className="max-w-4xl mx-auto">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
            <StatBox value="65%" label="Revenue Growth" />
            <StatBox value="55%" label="Consumer Experience" />
            <StatBox value="55%" label="AI & Technology" />
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <StatBox value="70%" label="Workforce Development" />
            <StatBox value="60%" label="Cybersecurity" />
          </div>
        </div>
      </section>

      {/* Digital Transformation Landscape */}
      <section className="bg-gradient-to-r from-white via-gray-50 to-white py-12 px-4">
        <div className="max-w-7xl mx-auto text-center mb-10">
          <h2 className="text-3xl font-bold text-gray-800">Digital Transformation Landscape</h2>
          <p className="text-sm text-gray-500 mt-2">Empowering Healthcare Through Smart Technologies</p>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6 max-w-7xl mx-auto">
          {/* Card 1 */}
          <InfoCard
            title="AI Leadership"
            items={["77% of leaders prioritize AI", "73% plan to use Generative AI", "Market growth $4B to $46B"]}
          />

          {/* Card 2 */}
          <InfoCard
            title="XR/VR Revolution"
            items={["68% medical schools use VR", "71% doctors prefer VR training", "84% believe positive impact"]}
          />

          {/* Card 3 */}
          <InfoCard
            title="Digital Learning"
            items={["Remote Acceleration", "AI personalizes training paths", "Enhance learning engagement"]}
