            items={["Digital front doors", "$265B home care by 2025", "Boost patient engagement"]}
          />
        </div>
      </section>
    </div>
  );
}

// Component: Challenge Card
function ChallengeCard({ icon, title, description }) {
  return (
    <div className="bg-white p-6 rounded-xl shadow flex items-center justify-between">
      <div>
        <h4 className="text-lg font-bold mb-1">{title}</h4>
        <p className="text-gray-600 text-sm">{description}</p>
      </div>
      {icon}
    </div>
  );
}

// Component: Stat Box
function StatBox({ value, label }) {
  return (
    <div className="h-[100px] border rounded shadow flex flex-col justify-center items-center">
      <h2 className="font-semibold mb-1 text-purple-600 text-xl">{value}</h2>
      <p className="text-sm text-center">{label}</p>
    </div>
  );
}

// Component: Info Card (used in Digital Transformation Landscape)
function InfoCard({ title, items }) {
  return (
    <div className="bg-white rounded-xl shadow p-6 hover:shadow-md transition">
      <h3 className="text-lg font-semibold text-gray-800 mb-3 text-center">{title}</h3>
      <ul className="text-sm text-gray-600 space-y-2">
        {items.map((item, index) => (
          <li key={index} className="border-t pt-2">{item}</li>
        ))}
      </ul>
    </div>
  );
}

export default HealthCare;
