import React from 'react';
import { FaUserMd, FaMoneyBillWave, FaMicrochip, FaHospitalAlt } from 'react-icons/fa';

function CriticalChallenges() {
  return (
    <section className="bg-white py-12 px-4">
      <div className="max-w-5xl mx-auto">
        {/* Title & Overview */}
        <h2 className="text-3xl font-bold text-gray-900 mb-2">Industry Challenges & Priorities</h2>
        <h3 className="text-xl font-normal text-gray-800 mb-4">Overview</h3>
        <p className="text-gray-700 mb-10 max-w-3xl">
          The healthcare industry is evolving through AI, XR, & digital learning—driving smarter care, cost efficiency, & patient engagement.
          Amid workforce & tech challenges, digital solutions empower providers to innovate, and scale resiliently.
        </p>

        {/* Critical Challenges Title */}
        <h3 className="text-2xl font-medium text-center mb-8">Critical Challenges</h3>

        {/* Grid of Cards */}
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
          <ChallengeCard
            icon={<FaUserMd className="text-purple-600 text-4xl mx-auto mb-4" />}
            title="Workforce Crisis"
            description="100K+ critical worker shortage by 2028, 50% burnout rate persists"
          />
          <ChallengeCard
            icon={<FaMoneyBillWave className="text-purple-600 text-4xl mx-auto mb-4" />}
            title="Financial Pressure"
            description="Rising costs. <2% operating margins, revenue inefficiencies"
          />
          <ChallengeCard
            icon={<FaMicrochip className="text-purple-600 text-4xl mx-auto mb-4" />}
            title="Technology Gaps"
            description="190+ ransomware attacks, 137TB daily data, workflow inefficiencies"
          />
          <ChallengeCard
            icon={<FaHospitalAlt className="text-purple-600 text-4xl mx-auto mb-4" />}
            title="Access Barriers"
            description="26-day wait times, 4 in 10 can’t afford care, cost-driven skipping"
          />
        </div>
      </div>
    </section>
  );
}

// Card Component
function ChallengeCard({ icon, title, description }) {
  return (
    <div className="bg-white border rounded-lg shadow-md p-6 text-center">
      {icon}
      <h4 className="text-lg font-semibold text-gray-900 mb-2">{title}</h4>
      <p className="text-sm text-gray-700">{description}</p>
    </div>
  );
}

export default CriticalChallenges;
